import { FETCH_MESSAGES_BEGIN, FETCH_MESSAGES_FAULURE, FETCH_MESSAGES_SUCCESS } from '../actions/';

const initState = {
    messages: [],
    loading: false,
    error: null
};

const backendReducer = (state = initState, action) => {
    switch (action.type){
        // case 'CREATE_MESSAGES':{ 
        //     console.log('created messages', action.message)
        //     return state;
        //     }
        case 'FETCH_MESSAGES_BEGIN':
            // Mark the state as "loading" so we can show a spinner or something
            // Also, reset any errors. We're starting fresh.
                return {
                    ...state,
                    loading: true,
                    error: null
                };
        case 'FETCH_MESSAGES_FAILURE':
            // The request failed. It's done. So set loading to "false".
            // Save the error, so we can display it somewhere.
            // Since it failed, we don't have items to display anymore, so set `items` empty.
            //
            // This is all up to you and your app though:
            // maybe you want to keep the items around!
            // Do whatever seems right for your use case.
            return {
                ...state,
                loading: false,
                error: action.payload.error,
                items: []
                };
        case 'FETCH_MESSAGES_SUCCESS':
            // All done: set loading "false".
            // Also, replace the items with the ones from the server
            return {
                ...state,
                loading: false,
                items: action.payload.messages
            };
        default:
            return state;
        }
    }

export default backendReducer;
