import React, { Component } from 'react';
import Messages from './Messages';
import AddMessage from './AddMessage';
import { connect } from 'react-redux';
import { createMessages, fetchMessages } from '../actions/backendActions';


class MessageBoardDisplay extends Component {
/*     state = {
        name: '',
        message: '',
        id: 2
    }

    generateInitialMsg = () => {
        return this.props.createMessages(this.state)
    }; */

    componentDidMount(){
        this.props.dispatch(fetchMessages());
    }

    render(){
        //console.log(this.props);
        //destructure to get messages object off props
        //const { messages } = this.props;
        //this.generateInitialMsg();

        const { error, loading, messages } = this.props;

        if (error) {
            return <div>Error! {error.message}</div>;
        }
        if (loading) {
            return <div>Loading...</div>;
        }
        return(

            <div>
                {/* pass { messages down into messages component } */}
                <Messages messages = { messages }/>
                <AddMessage addMessage={this.addMessage}/>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        //messages: state.message.messages
        messages: state.messages.items,
        loading: state.messages.loading,
        error: state.messages.error
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createMessages: (message) => dispatch(createMessages(message))
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(MessageBoardDisplay);
