import React from 'react';
import { connect } from 'react-redux';

const Message = ({messages, deleteMessage}) => {
    const messageList = messages.map(message => {
                return (
                    <div className="message" key={ message.id }>
                        <div>Name:{ message.Name } </div>
                        <div>Message:{ message.Message } </div>
                        <button onClick={() => {deleteMessage(message.id)}}>Delete message</ button>
                    </div>
                )
                })
        return (
            <div className="messageList">
                { messageList }
            </div>
        )
    }

    const mapStateToProps= (state, ownProps) => {
        let id = ownProps.match.params.message.id;
        return {
            message: state.messages.find(message => message.id === id)
        }
    }

export default connect(mapStateToProps)(Message)
