        let id = this.props.match.params.message_id;
        let messages = this.props.messages;
        let  messageId = messages.id.toString();
        function getClickedMessage(messageId,id){
            return (messageId === id);
        }
        let message = messages.find(getClickedMessage);
        console.log(this.props)
        console.log(getClickedMessage(messages,id))
        return(
        <div>
            <h4>yo yo yo</h4>
            <h4>{id}</h4>
            <h3>{message}</h3>
