import React, { Component } from 'react';
import Messages from './Messages';
import AddMessage from './AddMessage';
import { connect } from 'react-redux';
import { createMessages } from '../actions/backendActions';


class MessageBoardDisplay extends Component {
    state = {
        name: '',
        message: '',
        id: 2
    }
    generateInitialMsg = () => {
        return this.props.createMessages(this.state)
    };

    render(){
        generateInitialMsg();
        console.log(this.props);
        //destructure to get messages object off props
        const { messages } = this.props;

        return(

            <div>
                {/* pass { messages down into messages component } */}
                <Messages messages = { messages }/>
                <AddMessage addMessage={this.addMessage}/>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        messages: state.message.messages
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        createMessages: (message) => dispatch(createMessages(message))
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(MessageBoardDisplay);
