export const ADD_MESSAGE = 'ADD_MESSAGE'
export const DELETE_MESSAGE = 'DELETE_MESSAGE'
export const INITIAL_MESSAGES = 'INITIAL_MESSAGES'

// not used in setup as is because actions are called in components and then adjusted
// in reducer.

export function addMessage(message){
    return { type: ADD_MESSAGE, payload: message }
}

export function initialMessages(message){
    return dispatch => {
        dispatch(fetchMessagesBegin());
        return fetch('http://localhost:3001/users')
        .then(res => res.json())
        .then(json => {
                dispatch(fetchMessagesSuccess(json));
                })
            .catch(error => dispatch(fetchMessagesFailure(error)));
            };
    };

//pass index or message
export function deleteMessage(message){
    return { type: DELETE_MESSAGE, payload: message }
}
