import React, { Component } from 'react';
import { connect } from 'react-redux';
import Messages from './Messages';
import MesageBoardDisplay from './MessageBoardDisplay';

class ClickedMessage extends Component {
    render(){
        let id = this.props.match.params.message_id;
        const result = this.props.messages.find(message => (message.id.toString() === id));
        //console.log(result)
        return(
        <div>
            <h4>yo yo yo</h4>
            <h4>{id}</h4>
            <h3>{result.map(result => {message.name})}</h3>
        </div>
        )
    }
}

const mapStateToProps = (state) => {
    console.log(state)
    return {
        messages: state.messages
    }
}


export default connect(mapStateToProps)(ClickedMessage);
