import { ADD_MESSAGE } from "../actions";
import { DELETE_MESSAGE } from "../actions";
import Messages, { state } from "../components/Messages";

const initialMessages = {
    messages: [
            {name: 'Alvin', message: 'Hi', id: 1},
            {name: 'Todd', message: 'Hello', id: 2},
            {name: 'Kawhi', message: 'What it do ?', id: 3}
            ]
        }

// reducer function takes two arguments: 1) state (set with above array) & 2) an action
// reducer returns a new state based on the action, updating the given(old) state accordingly
const messageReducer = (state = initialMessages, action) => {
    console.log(state);
    switch (action.type){
        case 'INITIAL_MESSAGES':
            console.log(action);
            let messagesExpress = []; 
            function getInitialMessages(){
                fetch('/users')
                .then(res => res.json())
                .then(messagesExpress => this.setState({ messagesExpress }));
            };
            getInitialMessages();
            //return new state - initial messages array
            return {
                messages: messagesExpress
                };

        case 'DELETE_MESSAGE':
            console.log(action);  
            let newMessages = state.messages.filter(message => {
                return action.id !== message.id
            });
            return {
                messages: newMessages
                };

        case 'ADD_MESSAGE':
            console.log(action);
            const newState = state.messages.slice(0);
            newState.push(action.payload);
            return {
                messages: newState
            };

        default:
            console.log(state); 
            return state;

    }
}

export default messageReducer
