import React, { Component } from 'react';
import Messages from './Messages';
import AddMessage from './AddMessage';
import { connect } from 'react-redux';


class MessageBoardDisplay extends Component {
    state = {messagesExpress: []}

    componentDidMount(){
        fetch('/users')
        .then(res => res.json())
        .then(messagesExpress => this.setState({ messagesExpress }));
        console.log(this.state);
    }


    render(){

        return(
            <div>
                <Messages messages = {this.props.messages}/>
                <AddMessage addMessage={this.addMessage}/>
            <div>
                    <h1>ExpressMessages</h1>
                    <ul className = "messageList">
                        {this.state.messagesExpress.map(message => 
                        <div className = "message" key={message.id}>
                            <div>Name: {message.name}</div> 
                            <div>Message: {message.message}</div> 
                        </div>
                        )}
                    </ul>
                </div>
            </div>
        );
    }
}

const mapStateToProps = (state) => {
    return {
        messages: state.messages
    }
}

const mapDispatchToProps = (dispatch) => {
    return {
        deleteMessage: (id) => { dispatch ({ type: 'DELETE_MESSAGE', id: id }) }
    }
}


export default connect(mapStateToProps, mapDispatchToProps)(MessageBoardDisplay);
